import ExpenseDate from './ExpenseDate';
import Card from '../UI/Card';
import React, { useState } from 'react';

import './ExpenseItem.css'

const ExpenseItem = (props) =>{
    let title = props.title;
    const clickHandler = () => {
        title = "a new title";
    }

    return(
        //must have only ONE root element, and then as many children as you want
        <Card className='expense-item'>
            <ExpenseDate date={props.date}/>
            <div className="expense-item__description">
                <h2>{title}</h2>
                <div className="expense-item__price">${props.amount}</div>
            </div>
            {/*the reason we only point to clickHandler and not call it (i.e clickHandler() ) 
              *is because the function call is JavaScript which happens when the element is loaded
              *we just need to point to the function when the "onClick" event happens (too early)
              */}
            <button onClick={clickHandler}>Change Title</button>            
        </Card>
    );
}

export default ExpenseItem;
